import time

# Initialize a global variable to count comparisons
comparison_count = 0

def bubble_sort(list):
    global comparison_count  # Use the global comparison_count variable

    if len(list) <= 1:
        return list

    start = 0
    end = len(list) - 1

    # Outer loop for each pass
    for _ in range(end):
        # Inner loop for comparisons and swapping
        for i in range(start, end):
            if list[i] > list[i + 1]:
                # Swap elements if they are in the wrong order
                temp = list[i]
                list[i] = list[i + 1]
                list[i + 1] = temp
                comparison_count += 1  # Increment comparison count

        end -= 1  # Reduce the end index for the next pass

    return list

